import firebase from 'firebase/compat/app';
import 'firebase/compat/storage';
import { environment } from 'src/environments/environment';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
firebase.initializeApp(environment.firebaseConfig);
export class BusinessService {
    constructor(http) {
        this.http = http;
        this.storeRef = firebase.app().storage().ref();
        this.API_URI = 'http://localhost:3000/api'; //Como atributo la dirección del servidor
    }
    getTheBusiness() {
        return this.http.get(`${this.API_URI}/business`);
    }
    getBusiness(id) {
        return this.http.get(`${this.API_URI}/business/${id}`);
    }
    deleteBusiness(id) {
        return this.http.delete(`${this.API_URI}/business/${id}`);
    }
    saveBusiness(business) {
        return this.http.post(`${this.API_URI}/business/`, business);
    }
    verifyBusiness(business) {
        return this.http.get(`${this.API_URI}/business/verify-isExistBusiness/${business}`);
    }
    updateBusiness(id, updateBusiness) {
        return this.http.put(`${this.API_URI}/business/${id}`, updateBusiness);
    }
    categoriesList() {
        return this.http.post(`${this.API_URI}/business/get-categoriesList`, null);
    }
    async loadImg(email, imgBase64) {
        try {
            let respuesta = await this.storeRef.child("users/" + email).putString(imgBase64, 'data_url');
            return respuesta.ref.getDownloadURL();
        }
        catch (err) {
            console.log(err);
            return null;
        }
    }
}
